// ==========================================
// SINGLE PROCESSOR: DECODE & FORMAT
// ==========================================

// 1. Get the Data
var uplink = msg.payload.uplink_message;
var deviceId = msg.payload.end_device_ids.device_id;


// Safety Check
if (!uplink || !uplink.frm_payload) return null;


var final_values = {};


// 2. DECODING LOGIC
// A. If it's an Air Sensor (AM102), it's often pre-decoded. Use that.
if (uplink.decoded_payload && Object.keys(uplink.decoded_payload).length > 0) {
    final_values = uplink.decoded_payload;
}
// B. If it's a Soil Sensor (EM500), we MUST decode it manually.
else {
    var bytes = Buffer.from(uplink.frm_payload, 'base64');
    var i = 0;
    while (i < bytes.length) {
        var channel_id = bytes[i++];
        var channel_type = bytes[i++]; // Skip the type byte


        // Battery (Channel 1)
        if (channel_id === 0x01) {
            final_values.battery = bytes[i++];
        }
        // Temperature (Channel 3)
        else if (channel_id === 0x03) {
            var val = bytes[i] | (bytes[i + 1] << 8);
            if (val > 0x7FFF) val -= 0x10000;
            final_values.temperature = val / 10;
            i += 2;
        }
        // Moisture (Channel 4)
        else if (channel_id === 0x04) {
            final_values.moisture = bytes[i++] / 2;
            // Fix for 2-byte moisture alignment
            if (i < bytes.length && bytes[i] === 0x00 && bytes[i - 1] === 0x00) i++;
        }
        // Conductivity (Channel 5)
        else if (channel_id === 0x05) {
            final_values.conductivity = bytes[i] | (bytes[i + 1] << 8);
            i += 2;
        }
        else { i += 2; } // Safety skip
    }
}


// 3. DATABASE FORMATTING
var sensorType = "unknown";
if (deviceId.includes("soil") || deviceId.includes("em500")) sensorType = "soil_sensor";
else if (deviceId.includes("am102")) sensorType = "air_sensor";
else if (deviceId.includes("water")) sensorType = "water_sensor";
else if (deviceId.includes("em300") || deviceId.includes("mcs")) sensorType = "magnet_sensor";

// FIX: Set measurement explicitly
msg.measurement = "farm_metrics";

// FIX: Use standard [Fields, Tags] format
msg.payload = [
    final_values,
    {
        device: deviceId,
        type: sensorType
    }
];

return msg;
